var getUrlParameter = function getUrlParameter(sParam) {
    var sPageURL = decodeURIComponent(window.location.search.substring(1)),
        sURLVariables = sPageURL.split('&'),
        sParameterName,
        i;

    for (i = 0; i < sURLVariables.length; i++) {
        sParameterName = sURLVariables[i].split('=');

        if (sParameterName[0] === sParam) {
            return sParameterName[1] === undefined ? true : sParameterName[1];
        }
    }
}; 

(function($){ 

  var BCB = {
    init: function(){
      this.is_mobile = BCB_JS_VARS.is_mobile === '1' ? true : false;

      $('.bxslider').bxSlider();
      loadGravatars();
      this.checkIfLoggedIn();
      this.bindEvents();
      if(!this.is_mobile){
        this.loadVideos();
      }

      var loggedOut = getUrlParameter('loggedOut');
      var testing = getUrlParameter('testing');
      
      if(!$.cookie('intro-video-shown') === 'true' || !$.cookie('intro-video-shown')){
        if($('body').is('.home')){
          this.loadIntroVideo();
        }

      }

      console.log(testing);

      if(testing){
        $('.loggedInContent').show();
        console.log('show it');
      } else if(typeof testing === 'undefined') {
        $('.loggedOutContent').show();
        console.log('hide it');
      }

      if(!this.loggedIn){
        this.gateContent();
      } else if(this.loggedIn){
        console.log('logged in');
      }

      $("#Birthday, #birthday").mask("99/99/9999",{placeholder:"mm/dd/yyyy"});
    },

    bindEvents: function(){
      $('.open-menu').click(function(){
        var menu = $('#menu-main-menu');

        menu.slideToggle();
      });

      $('.details').click(function(e){
        e.preventDefault();

        if($(this).is('.open')){
          $(this).removeClass('open').text('Show Details').next().slideUp();
        } else {
          $(this).addClass('open').text('Hide Details').next().slideDown();
        }
      });
    },

    checkIfLoggedIn: function(){
      this.loggedIn = $('body').is('.logged-in');
    },

    loadIntroVideo: function(){
      var introVideo = videojs('intro_video', {
        autoplay: true,
        controlBar: {
          // muteToggle: false
        }
      });
      $('#intro').addClass('show-intro');

      $('#intro .close').on('click', function(){
        $('#intro').remove();
        $.cookie('intro-video-shown', 'true');
      });

      introVideo.on('ended', function(){
        $.cookie('intro-video-shown', 'true');
        $('#intro').remove();
      });
    },

    gateContent: function(){
      var gate = $('.gated');
      gate.append('<div class="gate"><div><a href="#join">JOIN THE BRIGADE</a></div></div>');
      // $('a', gate).click(function(e){
      //   e.preventDefault();
      // });
    },

    loadVideos: function(){
      $('.video-js').each(function(i, elm){
        var videoId = 'video-' + (i+1),
            poster = $(elm).data('poster') ? $(elm).data('poster') : null;
        
        $(this).attr('id', videoId);

        videojs(videoId, {
          autoplay: false,
          poster: poster
        });          
      });
    },
  }  

  $(function(){
    BCB.init();
  });

})(jQuery);